// Définir le sujet du message
msg.topic = "regul2";

// Obtenir le statut de régulation
var regulStatus = msg.payload.status.regul;

// Vérifier si regulStatus est une chaîne
if (typeof regulStatus === "string") {
    console.log("regul2 : " + regulStatus);

    // Vérifier si regul est WALK
    if (regulStatus === "WALK") {
        // Obtenir le temps actuel en timestamp
        let now = new Date().getTime();

        // Obtenir le temps du dernier envoi depuis le contexte du flow
        let lastSentTime = flow.get("lastSentTime") || 0;

        // Calculer la différence en minutes
        let diffMinutes = (now - lastSentTime) / 60000; // 60000 ms en 1 minute

        // Vérifier si 1 minutes se sont écoulées
        if (diffMinutes >= 1) {
            // Mettre à jour le temps du dernier envoi
            flow.set("lastSentTime", now);

            // Préparer le message pour l'envoi
            msg.payload = regulStatus;

            // Envoyer le message
            return msg;
        } else {
            // Si moins de 5 minutes se sont écoulées, ne rien faire
            console.log("Attente de 5 minutes nécessaire avant le prochain envoi.");
            return null;
        }
    }
}

// Si regul n'est pas WALK, ou si regulStatus n'est pas une chaîne, ne rien retourner
